import Customer from "./Customer";
import React from 'react';

//export all verables to another file
export default function Movement() {
  const customer = new Customer({ x: 0, y: 0 }, { x: 0, y: 0 });
  const keys = {
    w: { pressed: false },
    a: { pressed: false },
    s: { pressed: false },
    d: { pressed: false },
  };

  document.addEventListener("keyup", ({ key }) => {
    switch (key) {
      case "w":
        keys.w.pressed = false;
        break;
      case "a":
        keys.a.pressed = false;
        break;
      case "s":
        keys.s.pressed = false;
        break;
      case "d":
        keys.d.pressed = false;
        break;
    }
  });

  document.addEventListener("keydown", ({ key }) => {
    switch (key) {
      case "w":
        keys.w.pressed = true;
        customer.moveUp();
        console.log("move up" + customer.velocity.y);
        break;
      case "a":
        keys.a.pressed = true;
        customer.moveLeft();
        console.log("move left" + customer.velocity.x);
        break;
      case "s":
        keys.s.pressed = true;
        customer.moveDown();
        console.log("move down" + customer.velocity.y);
        break;
      case "d":
        keys.d.pressed = true;
        customer.moveRight();
        console.log("move right" + customer.velocity.x);
        break;
    }
  });

  document.addEventListener("keyup", ({ key }) => {
    switch (key) {
      case "w":
        keys.w.pressed = false;
        customer.stopMovingVertical();
        break;
      case "a":
        keys.a.pressed = false;
        customer.stopMovingHorizontal();
        break;
      case "s":
        keys.s.pressed = false;
        customer.stopMovingVertical();
        break;
      case "d":
        keys.d.pressed = false;
        customer.stopMovingHorizontal();
        break;
    }
  });
}
