type Velocity = {
    x: number;
    y: number;
  };
  type Position = {
    x: number;
    y: number;
  };
export default   class Customer {
    constructor(
      public position: Position,
      public velocity: Velocity,
      public radius: number = 10,
      public onStart: boolean = true
    ) {
      this.position = position;
      this.velocity = velocity;
      this.radius = 10;
      this.onStart = true;; 
    }
    static radius = 10;

    moveUp() {
      this.velocity.y -= 5;
      // console.log("move up");
    }
    moveDown() {
      // console.log("move down");
      this.velocity.y += 5;
    }
    moveLeft() {
      this.velocity.x -= 5;
      // console.log("move left");
    }
    moveRight() {
      this.velocity.x += 5;
      // console.log("move right");
    }



    draw = (ctx: CanvasRenderingContext2D | null) => {
      ctx!.beginPath();
      if (this.onStart) {
        this.position.x = 20;
        this.position.y = 320;
        this.onStart = false;
      }
      ctx!.arc(
        this.position.x,
        this.position.y,
        this.radius,
        0,
        Math.PI * 2
      );
      ctx!.fillStyle = "red";
      ctx!.fill();
      ctx!.closePath();

    };
    update(ctx: CanvasRenderingContext2D | null) {
      this.draw(ctx);
      this.position.x += this.velocity.x;  
      this.position.y += this.velocity.y;
    }
  }
