import React from 'react'
import { useState } from 'react'
import storeImgLayout from '../assets/images/supermarket.jpg'
import './StoreLayOut.css'
import BeverageModul from './BeverageModules/BeverageModul'
import CenteredModal from '../assets/UiKit/premade_componenets/Modals/VerticalModal'

export default function StoreLayOut() {
    const [isModelOpen, setIsModelOpen] = useState(false)

    function handleModal(status:boolean) {
        setIsModelOpen(status)
    }

    const BeverageArray = [
            {
              name: 'Coca Cola',
              price: 10
            },
            {
              name: 'Pepsi',
              price: 10
            },
            {
              name: 'Fanta',
              price: 10
            },
            {
              name: 'Sprite',
              price: 10
            },
            ]


  return (
    <div className={'store_layout'}>
    <img className='store_layout_background' src={storeImgLayout} alt={"store lay out"} />

    {/* store modules */}
    <button className='soft_drinks' ></button>
    <button className='natural_drinks'></button>
    <button className='beers'
    onClick={() => handleModal(true)}
    >X</button>
    <div>
    <BeverageModul rowsCount={4} columnsCount={4} bottlesArray={BeverageArray} />

    </div>

    {/* walkable space  */}
    {/* <button className='cart' /> */}

   {isModelOpen?
     <CenteredModal 
     isModelOpen={isModelOpen} 
     onHide={() => handleModal(false)}
     onShow={() => handleModal(true)}
     children={<BeverageModul rowsCount={4} columnsCount={4} bottlesArray={BeverageArray} />}
    /> 
    : null
    } 
  </div>
  )
}
