import React, { createContext, useContext } from 'react';

// Define the props interface
interface GlobalContextProviderProps {
  children: React.ReactNode;
}
type GlobalState = {
  customerControlState: boolean;
  setCount: React.Dispatch<React.SetStateAction<number>>;
};
const GlobalContext = createContext<GlobalState | undefined>(undefined);

function GlobalContextProvider(props: GlobalContextProviderProps) {

  const [customerControlState, setCustomerControlState] = React.useState(false);

  const globalState = {
    count,
    setCount,
  };

  return (
    <GlobalContext.Provider value={globalState}>
      {props.children}
    </GlobalContext.Provider>
  );
}

export function useGlobalState() {
  const globalState = useContext(GlobalContext);
  if (!globalState) {
    throw new Error('useGlobalState must be used within a GlobalContextProvider');
  }
  return globalState;
}

export default GlobalContextProvider;
