type Velocity = {
    x: number;
    y: number;
  };
  type Position = {
    x: number;
    y: number;
  };

export default   class Customer {
    constructor(
      public position: Position,
      public velocity: Velocity,
      public radius: number = 10,
      public onStart: boolean = true,
      public useCart: boolean = false,
      public movements: Movement[] = [],
      public startTime: number = 0,
      public currentIndex: number = 0
    ) {
      this.position = position;
      this.velocity = velocity;
      this.radius = 10;
      this.onStart = true;
      this.useCart = false;
      this.movements = [];
      this.startTime = 0;
      this.currentIndex = 0;
    }
    static radius = 10;

    moveUp() {
      this.velocity.y = -1;
      // this.movementRecording();
    }
    moveDown() {
      this.velocity.y = 1;
      // this.movementRecording();
    }
    moveLeft() {
      this.velocity.x = -1;
      // this.movementRecording();
    }
    moveRight() {
      this.velocity.x = 1;
      // this.movementRecording();
    }
    stopMovingVertical() {
      this.velocity.y = 0;
      // this.movementRecording();
    }
    stopMovingHorizontal() {
      this.velocity.x = 0;
      // this.movementRecording();
    }



moveCustomer() {
  const currentMovement = movements[currentIndex];
  const direction = currentMovement.direction;

  // Do something to move the customer in the specified direction

  currentIndex++;
  if (currentIndex < movements.length) {
    const nextMovement = movements[currentIndex];
    const delay = nextMovement.time - currentMovement.time;
    setTimeout(moveCustomer, delay);
  }
}



