import React, { createContext, useContext } from 'react';

// Create a new context for the global state
export const GlobalContext = createContext();


function GlobalContextProvider() {
  // Define your global state and actions here

  const [count, setCount] = React.useState(0);

  let children = null;
  // Create an object with all the values you want to share with child components
  const globalState = {
    count,
    setCount,
  };

  return (
    // Provide the global state to child components
    <GlobalContext.Provider value={globalState}>

        {children}

    </GlobalContext.Provider>
  );
}

