type Velocity = {
    x: number;
    y: number;
  };
  type Position = {
    x: number;
    y: number;
  };

export default   class Customer {
    constructor(
      public position: Position,
      public velocity: Velocity,
      public radius: number = 10,
      public onStart: boolean = true,
      public useCart: boolean = false
    ) {
      this.position = position;
      this.velocity = velocity;
      this.radius = 10;
      this.onStart = true;
      this.useCart = false;
    }
    static radius = 10;

    moveUp() {
      this.velocity.y = -1;
      // this.movementRecording();
    }
    moveDown() {
      this.velocity.y = 1;
      // this.movementRecording();
    }
    moveLeft() {
      this.velocity.x = -1;
      // this.movementRecording();
    }
    moveRight() {
      this.velocity.x = 1;
      // this.movementRecording();
    }
    stopMovingVertical() {
      this.velocity.y = 0;
      // this.movementRecording();
    }
    stopMovingHorizontal() {
      this.velocity.x = 0;
      // this.movementRecording();
    }
    // autoMovement() {
      // moveUp() {
