      // }
    // }
     recordMovement(
      movements: { x: number; y: number; time: DateConstructor }[],
      position: Position,
      startTime: number
    ) {
      const currentTime = Date.now();
      const elapsedSeconds = (currentTime - startTime) / 1000;
      const movement = {
        x: position.x,
        y: position.y,
        time: elapsedSeconds,
      };
      movements.push(movement);
    }




    draw = (ctx: CanvasRenderingContext2D | null) => {
      ctx!.beginPath();
      if (this.onStart) {
        this.position.x = 20;
        this.position.y = 320;
        this.onStart = false;
      }
      ctx!.arc(
        this.position.x,
        this.position.y,
        this.radius,
        0,
        Math.PI * 2
      )
      ctx!.fillStyle = "blue";
      ctx!.fill();
      ctx!.closePath();

    };
    update(ctx: CanvasRenderingContext2D | null) {
      this.draw(ctx);
      this.position.x += this.velocity.x;  
      this.position.y += this.velocity.y;
    }

    pullCart = () => {
      this.useCart = true;
      this.radius = 15;

      }
  }
