import React, { useRef, useEffect, useState, useMemo } from "react"; // Importing useRef and useEffect hooks
import "./CartRoutes.scss";
import Customer from "./Customers/Customer";
import Boundary from "./Map/Boundary";
import CartPickup from "./Map/CartPickup";
import Cameras from "./Map/Cameras";
import layoutMap from "./Map/layoutMap.json";
import routes from "./Map/routes.json";

type recordingArray = {
  direction: string;
  time: number;
  y: number;
  x: number;
};

interface RoutesProps {}

const CartRoutesAuto: React.FC<RoutesProps> = () => {

  const canvasRef = useRef<HTMLCanvasElement>(null);
  const boundaries = useMemo(() => {
    const boundaries: Boundary[] = [];
    return boundaries;
  }, []);
  const cartPickup = useMemo(() => {
    const cartPickup: CartPickup[] = [];
    return cartPickup;
  }, []);
  const cameras = useMemo(() => {
    const cameras: Cameras[] = [];
    return cameras;
  }, []);

  const customer = useMemo(() => {
    const customer: Customer = new Customer(
      {
        x: Boundary.width + Boundary.width / 2,
        y: Boundary.height + Boundary.height / 2,
      },
      { x: 0, y: 0 }
    );
    return customer;
  }, []);


 //create customers in loop 
  const customer2 = useMemo(() => {
    const customer2: Customer = new Customer(
      {
        x: Boundary.width + Boundary.width / 2,
        y: Boundary.height + Boundary.height / 2,
      },
      { x: 0, y: 0 }
    );
    return customer2;
  }, []);

  const customer3 = useMemo(() => {
    const customer3: Customer = new Customer(
      {
        x: Boundary.width + Boundary.width / 2,
        y: Boundary.height + Boundary.height / 2,
      },
      { x: 0, y: 0 }
    );
    return customer3;
  }, []);

  const customer4 = useMemo(() => {
    const customer4: Customer = new Customer(
      {
        x: Boundary.width + Boundary.width / 2,
        y: Boundary.height + Boundary.height / 2,
      },
      { x: 0, y: 0 }
    );
    return customer4;
  }, []);
const customers5 =useMemo(() => {
  const customers5: Customer = new Customer(
    {
      x: Boundary.width + Boundary.width / 2,
      y: Boundary.height + Boundary.height / 2,
    },
    { x: 0, y: 0 }
  );
  return customers5;
}, []);



  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas!.getContext("2d");
    canvas!.height = 470;
    canvas!.width = 600;
    boundaries.forEach((boundary) => {
      boundary.draw(ctx);
    });
    cartPickup.forEach((cartPick) => {
      cartPick.draw(ctx);
    });
    cameras.forEach((camera) => {
      camera.draw(ctx);
    });
    function animate() {
      requestAnimationFrame(animate);
      ctx!.clearRect(0, 0, canvas!.width, canvas!.height);
      let custumerArr= [customer,customer2,customer3,customer4,customers5]
      custumerArr.forEach(c => {

      boundaries.forEach((boundary) => {
        boundary.draw(ctx);
        if (
          c.position.y - c.radius +3+ c.velocity.y <=
            boundary.position.y + boundary.height &&
          c.position.x + c.radius -5 + c.velocity.x >=
            boundary.position.x &&
          c.position.y + c.radius -3 + c.velocity.y >=
            boundary.position.y &&
          c.position.x + c.radius -15 + c.velocity.x <=
            boundary.position.x + boundary.width
        ) {
          c.velocity.x = 0;
          c.velocity.y = 0;
        }

      });
      cartPickup.forEach((cartPick) => {
        cartPick.draw(ctx);

        if (
          c.position.y - c.radius +3+ c.velocity.y <=
            cartPick.position.y + cartPick.height &&
          c.position.x + c.radius -5 + c.velocity.x >=
            cartPick.position.x &&
          c.position.y + c.radius -3 + c.velocity.y >=
            cartPick.position.y &&
          c.position.x + c.radius -15 + c.velocity.x <=
            cartPick.position.x + cartPick.width
        ) {
          c.pullCart();
        }
      });
      cameras.forEach((camera) => {
        camera.draw(ctx);
        if (
          c.position.y - c.radius +3+ c.velocity.y <=
            camera.position.y + camera.height &&
          c.position.x + c.radius -5 + c.velocity.x >=
            camera.position.x &&
          c.position.y + c.radius -3 + c.velocity.y >=
            camera.position.y &&
          c.position.x + c.radius -15 + c.velocity.x <=
            camera.position.x + camera.width
        ) {
          c.velocity.x = 0;
          c.velocity.y = 0;
        }
      });
      c.update(ctx); 

    })
    }
    animate();
  });


function moveCustomer(steps: any[], index: number, customer: Customer) {
  if (index < steps.length) {
    let step = steps[index];

    if (step.direction === "right") {
      customer.moveRight();
    } else if (step.direction === "left") {
      customer.moveLeft();
    } else if (step.direction === "up") {
      customer.moveUp();
    } else if (step.direction === "down") {
      customer.moveDown();
    } else if (step.direction === "stop") {
      customer.stopMovingHorizontal();
      customer.stopMovingVertical();
    } else if (step.direction === "end") {
      customer.stopMovingHorizontal();
      customer.stopMovingVertical();
      return;
    }

    setTimeout(() => {
      moveCustomer(steps, index + 1, customer);
    }, step.time - (index === 0 ? 0 : steps[index - 1].time));
  }
}

//turn routes into array 


const stepsArr = Object.entries(routes);
const customerArray = [customer,customer2,customer3,customer4,customers5]
//loop through array and call moveCustomer function
// stepsArr.forEach((step, i) => {
  console.log(stepsArr[1]);

  moveCustomer(stepsArr[1], 0, customer);
// });







  const layoutArray = Object.entries(layoutMap);


  layoutArray.forEach((row, i) => {
    row.forEach((symbol, j) => {
      switch (symbol) {
        case "-":
          boundaries.push(
            new Boundary({
              x: Boundary.width * j,
              y: Boundary.height * i,
            })
          );
          break;

        case "+" :
          cartPickup.push(
            new CartPickup({
              x: CartPickup.width * j,
              y: CartPickup.height * i,
            })
          );
          break;

        case "x" :
          cameras.push(
            new Cameras({
              x: Cameras.width * j,
              y: Cameras.height * i,
            })
          );
          break;
      }
    });
  });


  return <canvas className="canvas" ref={canvasRef}></canvas>
};


export default CartRoutesAuto;
